    public static int createSortedArray(int[] instructions) {

        // merge
        Set<Integer> s = new TreeSet<>();
        for (int ins : instructions
        ) {
            s.add(ins);
        }

        Map<Integer, Integer> map = new HashMap<>();
        int idx = 0;
        for (int i : s) {
            map.put(i, idx++);
        }

        // construct tree
        SegNode root = build(0, s.size());

        long ret = 0L;
        for (int ins : instructions
        ) {
            int id = map.get(ins);
            ret += Math.min(count(root, 0, id - 1), count(root, id + 1, s.size()));
            insert(root, id);
        }

        return (int) (ret % 1000000007);
    }

    public static SegNode build(int left, int right) {
        SegNode root = new SegNode(left, right);

        if (left != right) {
            root.leftNode = build(left, (left + right) >> 1);
            root.rightNode = build(((left + right) >> 1) + 1, right);
        }
        return root;
    }

    public static void insert(SegNode root, int val) {
        root.add++;
        if (root.left != root.right) {
            if (val <= ((root.left + root.right) >> 1)) insert(root.leftNode, val);
            else insert(root.rightNode, val);
        }
    }

    public static long count(SegNode root, int leftVal, int rightVal) {
        if (root.add == 0) return 0;
        else if (rightVal < root.left || leftVal > root.right) return 0;
        else if (root.left == root.right) return root.add;
        else if (leftVal <= root.left && root.right <= rightVal) return root.add;
        else return count(root.leftNode, leftVal, rightVal) + count(root.rightNode, leftVal, rightVal);
    }

    static class SegNode {
        SegNode leftNode = null;
        SegNode rightNode = null;

        int left;
        int right;
        long add = 0;

        public SegNode(int left, int right) {
            this.left = left;
            this.right = right;
        }
    }
